static void __Startup() {
        
    xmlInitParser();//<->xmlCleanupParser();
    xsltInit();
    
    xmlLoadExtDtdDefaultValue = XML_DETECT_IDS | XML_COMPLETE_ATTRS;
    xmlSubstituteEntitiesDefault(1);
    xmlIndentTreeOutput = 1;//xmlGlobalState
}

static void OnStartup() {
    
#ifdef VERSIONWIN
    PA_RunInMainProcess((PA_RunInMainProcessProcPtr)__Startup, NULL);
#else
    __Startup();
#endif
}

static void __Exit() {

    xsltCleanupGlobals();
    xmlCleanupParser();//<->xmlInitParser();
}

static void OnExit() {
    
#ifdef VERSIONWIN
    PA_RunInMainProcess((PA_RunInMainProcessProcPtr)__Exit, NULL);
#else
    __Exit();
#endif
}

#pragma mark -

void PluginMain(PA_long32 selector, PA_PluginParameters params) {
    
	try
	{
        switch(selector)
        {
            case kInitPlugin :
            case kServerInitPlugin :
                OnStartup();
                break;
                
            case kDeinitPlugin :
            case kServerDeinitPlugin :
                OnExit();
                break;
                
			// --- xmlsec
            
			case 1 :
				xmlsec_sign(params);
				break;
			case 2 :
                xmlsec_hash(params);
				break;
            case 3 :
                xmlsec_x509(params);
                break;
        }

	}
	catch(...)
	{

	}
}

